import { motion, AnimatePresence } from "framer-motion"
import { useContext } from "react"
import Feedback from "./Feedback"
import PropTypes from "prop-types"
import { default as Context } from "../Context/Feedback"

function FeedbackList({ handleDelete }) {
  const { feedback } = useContext(Context)
  let feedbackWithComments = feedback.filter((item) => item.comments !== null)
  if (!feedbackWithComments || feedbackWithComments.length === 0) {
    return <p>No Feedback Found</p>
  }
  return (
    <div className='feedback-list'>
      <AnimatePresence>
        {feedbackWithComments.map((item) => (
          <motion.div
            key={item.id}
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
          >
            <Feedback
              key={item.id}
              feedback={item}
              handleDelete={handleDelete}
            />
          </motion.div>
        ))}
      </AnimatePresence>
    </div>
  )
}

FeedbackList.propType = {
  feedback: PropTypes.arrayOf(
    PropTypes.shape({
      id: PropTypes.string.isRequired,
      rating: PropTypes.number.isRequired,
      comments: PropTypes.string,
    })
  ),
  handleDelete: PropTypes.func.isRequired,
}

export default FeedbackList
