function Stats({ feedback }) {
  const Reviews = feedback.length
  let Sum = feedback.reduce((a, b) => a + b.rating, 0)
  const Average = (Sum / Reviews).toFixed(1)
  return (
    <div className="feedback-stats">
      <h4>{Reviews} Reviews</h4>
      <h4>Average Rating of {Average}</h4>
    </div>
  )
}
export default Stats
